var map = new L.Map('map-container', {
  center : new L.LatLng(-10.0, -58.0),
  zoom : 6,
  layers : [bingroad],
  zoomControl : true
});

function processTMS(data) {
  function onEachFeature(feature, layer) {

    var popupContent = "<p>I started out as a GeoJSON " +
      feature.geometry.type + ", but now I'm a Leaflet vector!</p>";

    if (feature.properties && feature.properties.popupContent) {
      popupContent += feature.properties.popupContent;
    }

    layer.bindPopup(popupContent);

    map.on("moveend", function(e){
      if(layer.getBounds().contains(map.getCenter())) {
        layer.setStyle({fillColor: '#FF0000'});
      }
      else {
        // layer.setStyle({fillColor: '#0000FF'});
      }
    });
  }

  var geojson = L.geoJson(data,{
    onEachFeature: onEachFeature
  });

  geojson.addTo(map);
}

$.getJSON("geojson/rapideye/pampas.json", function(data){
  processTMS(data);
});


